import React, { useState, useEffect } from 'react';
import './Admin.css';
import { BASE_URL } from './Services/baseURL';

// Users will be fetched from backend

// Recipes will be fetched from backend


function Admin() {
    const [users, setUsers] = useState([]);
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        // Fetch users
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:4000/admin/user/all', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (!response.ok) throw new Error('Failed to fetch users');
                const data = await response.json();
                // Map user data to required fields
                const mapped = data.map(user => ({
                    id: user._id,
                    email: user.email,
                    username: user.username,
                    profilePhoto: user.profile || 'https://randomuser.me/api/portraits/lego/1.jpg',
                }));
                setUsers(mapped);
            } catch (err) {
                setUsers([]);
            }
        };

        // Fetch recipes
        const fetchRecipes = async () => {
            try {
                const response = await fetch('http://localhost:4000/admin/posts/all', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (!response.ok) throw new Error('Failed to fetch recipes');
                const data = await response.json();
                const mapped = data.map(recipe => ({
                    id: recipe._id,
                    photo: recipe.recipeImage,
                    name: recipe.recipename,
                    description: recipe.make,
                    likes: recipe.likes ? recipe.likes.length : 0,
                }));
                setRecipes(mapped);
            } catch (err) {
                setRecipes([]);
            }
        };

        fetchUsers();
        fetchRecipes();
    }, []);



    



    const handleDeleteUser = async (id) => {
        try {
            const response = await fetch(`http://localhost:4000/admin/user/delete/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            if (!response.ok) throw new Error('Failed to delete user');
            setUsers(users.filter(user => user.id !== id));
        } catch (err) {
            alert('Failed to delete user.');
        }
    };

    const handleDeleteRecipe = (id) => {
        setRecipes(recipes.filter(recipe => recipe.id !== id));
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <div className="admin-section">
                <h3>Users List</h3>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Profile Photo</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td><img src={`${BASE_URL}/uploads/${user.profilePhoto}`} alt={user.username} className="profile-photo" /></td>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td><button className="delete-btn" onClick={() => handleDeleteUser(user.id)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="admin-section">
                <h3>Recipes List</h3>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Photo</th>
                            <th>Name</th>
                            <th>Description</th>
                            <th>Likes</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recipes.map(recipe => (
                            <tr key={recipe.id}>
                                <td><img src={`${BASE_URL}/uploads/${recipe.photo}`} alt={recipe.name} className="recipe-photo" /></td>
                                <td>{recipe.name}</td>
                                <td>{recipe.description}</td>
                                <td>{recipe.likes}</td>
                                <td><button className="delete-btn" onClick={() => handleDeleteRecipe(recipe.id)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Admin;
