import React, { useState, useEffect } from 'react';
import './Admin.css';

const initialUsers = [
    { id: 1, email: 'alice@example.com', username: 'alice', profilePhoto: 'https://randomuser.me/api/portraits/women/1.jpg' },
    { id: 2, email: 'bob@example.com', username: 'bob', profilePhoto: 'https://randomuser.me/api/portraits/men/2.jpg' },
    { id: 3, email: 'carol@example.com', username: 'carol', profilePhoto: 'https://randomuser.me/api/portraits/women/3.jpg' },
];

// Recipes will be fetched from backend


function Admin() {

    const [users, setUsers] = useState(initialUsers);
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const response = await fetch('http://localhost:4000/admin/posts/all', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (!response.ok) throw new Error('Failed to fetch recipes');
                const data = await response.json();
                // If data is an array of recipes
                const mapped = data.map(recipe => ({
                    id: recipe._id,
                    photo: recipe.recipeImage, // You may need to prepend a base URL if not absolute
                    name: recipe.recipename,
                    description: recipe.make,
                    likes: recipe.likes ? recipe.likes.length : 0,
                }));
                setRecipes(mapped);
            } catch (err) {
                setRecipes([]);
            }
        };
        fetchRecipes();
    }, []);

    const handleDeleteUser = (id) => {
        setUsers(users.filter(user => user.id !== id));
    };

    const handleDeleteRecipe = (id) => {
        setRecipes(recipes.filter(recipe => recipe.id !== id));
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <div className="admin-section">
                <h3>Users List</h3>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Profile Photo</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td><img src={user.profilePhoto} alt={user.username} className="profile-photo" /></td>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td><button className="delete-btn" onClick={() => handleDeleteUser(user.id)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="admin-section">
                <h3>Recipes List</h3>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Photo</th>
                            <th>Name</th>
                            <th>Description</th>
                            <th>Likes</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recipes.map(recipe => (
                            <tr key={recipe.id}>
                                <td><img src={`${BASE_URL}/uploads/${recipe.photo}`} alt={recipe.name} className="recipe-photo" />
                                    

                                </td>
                                <td>{recipe.name}</td>
                                <td>{recipe.description}</td>
                                <td>{recipe.likes}</td>
                                <td><button className="delete-btn" onClick={() => handleDeleteRecipe(recipe.id)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Admin;
